import React from "react";
import "react-quill/dist/quill.snow.css";
import ReactQuill from "react-quill";
import {
  Card,
  CardBody,
  CardImg,
  Form,
  FormInput,
  Button,
  Input,
  CardHeader
} from "shards-react";
import AdminDashboard from ".";
import "./CSS/addJob.css";

import AdminFooter from "./admin-footer";
import RangeSalary from "./rangeSalary";
import cardImage from "../img/code-820275.jpg";

// The page is built on Shards React Library

function AddJob() {


    function displayImage(event){
var reader = new FileReader();
reader.onload = function()
{
    var output = document.getElementById("imagePreview");
    output.src = reader.result;
}
reader.readAsDataURL(event.target.files[0]);
    }
  return (
    <div>
      <AdminDashboard />
      <div>.</div>
      <Card small className="addForm jobForm">
        <div className="form-title">POST A NEW JOB</div>

        <CardBody>
          <Form className="add-new-post">
            <FormInput size="lg" className="job-col1" placeholder="Job Title" />
            {/* <div>Validity</div> */}
            <FormInput
              size="lg"
              className="mb-3 job-col1 job"
              placeholder="Validity"
              type="date"
            />

            {/* Import the File handling the Salary Range */}
            <RangeSalary />

            <p className="jobImgInd">JOB IMAGE PREVIEW</p>
            <CardImg src={cardImage} id="imagePreview" className="JobImage" />
            {/* <FormInput
              size="lg"
              className="selectImage"
              placeholder="Job Title"
              type="file"
            /> */}
             <input type="file" size="lg"
              className="selectImage"
              placeholder="Job Title" onChange={displayImage}/>
            <ReactQuill
              className="add-new-post__editor mb-1 jobDescription"
              placeholder="Description of the Job here"
            />
          </Form>
        </CardBody>
        <div className="publishJob">
          <button className="publish-btn">Publish</button>
        </div>
      </Card>

      {/* <div style={{textAlign:"center"}}>Test</div> */}
      {/* <AdminFooter /> */}
    </div>
  );
}

export default AddJob;
