import React, { useState, Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
// import About from './about';
import {ScrollToHOC, ScrollArea} from "react-scroll-to";
import "./App.css";

function Head(props) {
  var [navBar, setPos] = useState(false);

  function onScreenLoad() {
    alert("hey");
  }
  var setValue = 0;
  // if(setValue === 0){
  //   document.getElementById("menu").style.display = "none";

  // }
  function menuNavBar() {
    setValue = 1;
    if (navBar === false) {
      document.getElementById("menu").style.display = "initial";

      setPos(true);
    } else {
      document.getElementById("menu").style.display = "none";

      // document.getElementById("menu").style.height = "2";

      setPos(false);
    }
  }

  return (
    <div onload={onScreenLoad}>
      <div className="header-bar">
        <header>
          <div className="logo">Logo</div>
          <div className="hd-col">
            <Link to="/" className="hd">
              HOME
            </Link>

            <Link to="/about/" className="hd">
              ABOUT
            </Link>

            <Link to="/contact/" className="hd">
              CONTACT US
            </Link>

            <Link to="/applications/" className="hd">
              APPLICATIONS
            </Link>
            {/* <ScrollArea style={{ height: 100 }}>
            <a onClick={() => props.scroll(0, 5000)}>
                Scroll to Bottom
            </a>
        </ScrollArea> */}
            {/* <Link to="/signin/" className="hd">SIGNIN</Link> */}
          </div>
          <div className="hamburg" onClick={menuNavBar}>
            MENU
          </div>
        </header>

        <div className="menu" id="menu">
          <div className="navbar" id="navbar">
            <hr />
            <Link to="/" className="hdm home">
              HOME
            </Link>
            <hr />

            <Link to="/aboutus/" className="hdm">
              ABOUT
            </Link>
            <hr />

            <Link to="/contact/" className="hdm">
              CONTACT US
            </Link>
            <hr />

            <Link to="/applications/" className="hdm">
              APPLICATIONS
            </Link>
            <hr />
            {/* <Link to="/signin" className="hdm">SIGNIN</Link>
            <hr /> */}
          </div>

        </div>
      </div>
    </div>
  );
}

export default ScrollToHOC(Head);
