import React from "react";
import "react-quill/dist/quill.snow.css";
import ReactQuill from "react-quill";
import AdminDashboard from ".";
import "./CSS/addBlog.css";
import AdminFooter from "./admin-footer";
import {
    Card,
    CardBody,
    CardImg,
    Form,
    FormInput
} from "shards-react";

import {
    useState,
    useEffect
} from "react";
const axios = require("axios").default;

const displayImage = event => {
    var reader = new FileReader();
    reader.onload = function() {
        var output = document.getElementById("imagePreview");
        output.src = reader.result;
    };
    reader.readAsDataURL(event.target.files[0]);
};

const createBlogPost = () => {
    const urlLink = "http://localhost:5000/blog/newpost";

    const dataObject = {
        POSTEDBY: "omosteve",
        BLOGTITLE: document.getElementById("blogTitle"),
        BLOGPOST: document.getElementById("blogPost").innerText,
        BLOGIMAGE: document.getElementById("blogImage").file
    };
    console.log(dataObject);
    axios({
            method: "POST",

            url: urlLink,

            data: dataObject
        })
        .then(function(response) {
            console.log(response);
        })
        .catch(function(err) {
            if (err.response.data.message === "Invalid Email") {

                document.getElementById("postFeedback").innerHTML = "Sorry, you're not authorized for this!";
                document.getElementById("postFeedback").style.color = "red";

            }
            console.log(err);
        });
};

const AddBlogPost = () => {
    document.title = "DarkBank | Admin | POST A NEW BLOG";

    return ( <
        div >
        <
        AdminDashboard / >
        <
        div > . < /div> <
        Card small className = "addForm jobForm" >
        <
        div className = "form-title" > ADD A NEW BLOG POST < /div>

        <
        div style = {
            {
                marginLeft: "1.1em"
            }
        }
        id = "postFeedback" > < /div> <
        CardBody >
        <
        Form className = "add-new-post" >
        <
        FormInput size = "lg"
        className = "mb-3"
        placeholder = "Your Post Title"
        id = "blogTitle" /
        >
        <
        p className = "jobImgInd" >
        UPLOAD AND PREVIEW BLOG POST IMAGE HERE!
        <
        /p>

        <
        CardImg id = "imagePreview"
        className = "JobImage" / >

        <
        input type = "file"
        size = "lg"
        className = "selectImage"
        placeholder = "Job Title"
        onChange = {
            displayImage
        }
        id = "blogImage" /
        >
        <
        ReactQuill className = "add-new-post__editor mb-1 jobDescription"
        id = "blogPost" /
        >
        <
        /Form> < /
        CardBody > <
        div className = "publish" >
        <
        button className = "publish-btn"
        onClick = {
            createBlogPost
        } >
        Publish <
        /button> < /
        div > <
        /Card>

        {
            /* <div style={{textAlign:"center"}}>Test</div> */
        } <
        AdminFooter / >
        <
        /div>
    );
};

export default AddBlogPost;
