import React from "react";
import AdminDashboard from ".";
import "react-quill/dist/quill.snow.css";
import ReactQuill from "react-quill";
import {
  Card,
  CardBody,
  Form,
  FormInput,
  Button,
  FormSelect
} from "shards-react";
import AdminFooter from "./admin-footer";
import { useState, useEffect } from "react";

function EditWebsite() {
  document.title = "DarkBank | Admin | EDIT THE WEBSITE CONTENT";

  const [sitePart, setSitePart] = useState("");
  const [content, setContent] = useState("");

  const updateBtn = () => {
    setContent(document.getElementById("content").innerText);
    const updateObject = {
      SITEPART: sitePart,
      CONTENT: content
    };

    console.log(updateObject);
  };
  return (
    <div>
      {/* <AdminDashboard /> */}
      <div> . </div>
      <Card small className="addForm">
        <div className="form-title"> EDIT / UPDATE THE WEBSITE BELOW </div>
        <CardBody>
          <Form className="add-new-post">
            <FormSelect
              size="lg"
              className="mb-3"
              placeholder="Select Edit Name"
              onChange={event => setSitePart(event.target.value)}
            >
              <option> Select Which Part to Edit </option>
              <option value="HOMEPAGECIRCLECONTENT"> Homepage Circle Content </option>
              {/* For Our serices category */}
              <option> Our Services - First Card - Front Title </option>
              <option> Our Services - First Card - Front Description </option>
              <option> Our Services - First Card - Back Content </option>
              <option> Our Services - Second Card - Front Title </option>
              <option> Our Services - Second Card - Front Description </option>
              <option> Our Services - Second Card - Back Content </option>
              <option> Our Services - Third Card - Front Title </option>
              <option> Our Services - Third Card - Front Description </option>
              <option> Our Services - Third Card - Back Content </option>
              {/* For contact us category */}
              <option> Contact Us - Email </option>
              <option> Contact Us - Telephone </option>
              {/* For Footer Category */} <option> Footer - Address </option>
              <option> Footer - Facebook URL </option>
              <option> Footer - Instagram URL </option>
              <option> Footer - Google Plus URL </option> {/* For About Us */}
              <option> About Us - About Us Content </option>
            </FormSelect>
            <ReactQuill className="add-new-post__editor mb-1" id="content" />
          </Form>
        </CardBody>
        <div className="publish">
          <button className="publish-btn" onClick={updateBtn}>
            {" "}
            Update{" "}
          </button>
        </div>
      </Card>
      <AdminFooter />
    </div>
  );
}

export default EditWebsite;
