import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import "./CSS/auth.css";
import { BrowserRouter as Router, Link } from "react-router-dom";
const axios = require("axios").default;

function Register() {
  document.title = "DarkBank | User | LOGIN TO DASHBOARD";

  function registerBtn(e) {
    e.preventDefault();

    document.getElementById("registerID").innerHTML = "Please wait!";

    const urlLink = "http://localhost:5000/auth/register/";

    const dataObject = {
      FIRSTNAME: "Steven",
      LASTNAME: "LastSteven",
      EMAIL: "testemail",
      PASSWORD: "testpass"
    };

    axios({
      method: "POST",

      url: urlLink,

      data: dataObject
    })
      .then(function(response) {
        console.log("seen", response);
        document.getElementById("registerID").innerHTML = "SIGN IN"; 
        document.getElementById("ApiResponse").innerHTML =
            "Your account has been successfully created! Kindly check your email to confirm.";      

      })
      .catch(function(err) {

        document.getElementById("registerID").innerHTML = "SIGN IN";       

        if (err.response.data.message === "Invalid Email") {
          document.getElementById("ApiResponse").innerHTML =
            "The email entered is not valid";
        }
        else  if (err.response.data.message === "Account Existed Already") {
          document.getElementById("ApiResponse").innerHTML =
            "This email is associated with an existing account";
        } else  if (err.response.data.message === "Invalid FIRSTNAME or LASTNAME") {
          document.getElementById("ApiResponse").innerHTML =
            "We're sorry, seems your first or last name contains a non alphabetic character";
        }
        else  if (err.response.data.message === "Account Failed to Create") {
          document.getElementById("ApiResponse").innerHTML =
            "We're sorry, an error occurred. Kindly try again";
        }else{
          document.getElementById("ApiResponse").innerHTML =
          "We're sorry, an error occurred. Kindly try again";
        }

      });
  }
  return (
    <div className="Authwrapper">
      <div className="Authwrapper fadeInAuthDown">
        <div id="AuthformContent">
          <div className="fadeInAuth first">CREATE ACCOUNT</div>
          <div className="ApiResponse" id="ApiResponse">
            Error
          </div>
          <form id="registerForm">
            <input
              type="text"
              className="loginInput fadeInAuth second"
              name="email"
              placeholder="Firstname"
            />
            <input
              type="text"
              className="loginInput fadeInAuth second"
              name="email"
              placeholder="Lastname"
            />
            <input
              type="text"
              className="loginInput fadeInAuth second"
              name="email"
              placeholder="Email"
            />
            <input
              type="password"
              className="loginInput fadeInAuth third"
              name="password"
              placeholder="Password"
            />
            <input
              type="password"
              className="loginInput fadeInAuth third"
              name="password"
              placeholder="Confirm password"
            />
            <button
              variant="contained"
              color="primary"
              className="btn-auth fadeInAuth fourth"
              id="registerID"
              onClick={registerBtn}
            >
              REGISTER
            </button>
            <div className="orBox">
              <Link to="/auth/signin/" className="signInLink">
                {" "}
                Already own an account?
              </Link>
              {/* <hr className="hrAuth" />
              OR
              <hr className="hrAuth" />
              <button
                variant="contained"
                color="primary"
                className="btn-auth fadeInAuth fourth orBtn"
              >
                SIGNIN
              </button> */}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
export default Register;
