import React from "react";
import "react-quill/dist/quill.snow.css";
import ReactQuill from "react-quill";
import AdminDashboard from ".";
import "./CSS/addBlog.css";
import AdminFooter from "./admin-footer";
import {
  Card,
  CardBody,
  CardImg,
  Form,
  FormInput,
  Button,
  Input,
  CardHeader
} from "shards-react";

const displayImage = (event) => {
  var reader = new FileReader();
  reader.onload = function() {
    var output = document.getElementById("imagePreview");
    output.src = reader.result;
  };
  reader.readAsDataURL(event.target.files[0]);
}

function AddBlogPost() {

  document.title = "DarkBank | Admin | POST A NEW BLOG";

  return (
    <div>
      <AdminDashboard />
      <div>.</div>
      <Card small className="addForm jobForm">
      <div className="form-title">ADD A NEW BLOG POST</div>

        <CardBody>
          <Form className="add-new-post">
            <FormInput
              size="lg"
              className="mb-3"
              placeholder="Your Post Title"
            />
            <p className="jobImgInd">UPLOAD AND PREVIEW JOB IMAGE HERE!</p>

<CardImg id="imagePreview" className="JobImage" />

<input
  type="file"
  size="lg"
  className="selectImage"
  placeholder="Job Title"
  onChange={displayImage}
/>
            <ReactQuill className="add-new-post__editor mb-1" />
          </Form>
        </CardBody>
        <div className="publish"><button className="publish-btn">Publish</button></div>

      </Card>

      {/* <div style={{textAlign:"center"}}>Test</div> */}
      <AdminFooter/>
    </div>
  );
}

export default AddBlogPost;
